mapper.select:
SELECT * FROM ORDER_INFO  WHERE

	status = #{status}


	status IN(0,1,2,3)


	AND orderCode = #{orderCode}


	AND paymentType = #{paymentType}


	AND shippingType = #{shippingType}


	AND orderDate LIKE concat(concat('%',#{dateSearch}), '%')


	AND name = #{name}


	AND tel = #{tel}


	AND email = #{email}

ORDER BY orderDate DESC
