mapper.insert:
INSERT INTO POST(
title
, blogId
, img
, url
, created
, subContent
, status
, content
, position
)VALUES(
#{dto.title}
, #{dto.blogId}
, #{dto.img}
, #{dto.url}
, NOW()
, #{dto.subContent}
, #{dto.status}
, #{dto.content}
, #{dto.position}
)

mapper.update:
UPDATE POST SET
title = #{dto.title}
,blogId = #{dto.blogId}
, content = #{dto.content}

,img = #{dto.img}

,url = #{dto.url}
,subContent= #{dto.subContent}
,status=#{dto.status}
,position=#{dto.position}
WHERE id= #{dto.id}

mapper.select:
SELECT * FROM POST WHERE blogId = #{id}

	AND status = #{status}
 ORDER BY id
