mapper.insert:
INSERT INTO POST(
title
, tag
, img
, url
, created
, subContent
, status
, content
, isHigh
, source
)VALUES(
#{dto.title}
, #{dto.tag}
, #{dto.img}
, #{dto.url}
, NOW()
, #{dto.subContent}
, #{dto.status}
, #{dto.content}
, #{dto.isHigh}
, #{dto.source}
)

mapper.update:
UPDATE POST SET
title = #{dto.title}
,tag = #{dto.tag}
, content = #{dto.content}

,img = #{dto.img}

,url = #{dto.url}
,subContent= #{dto.subContent}
,status=#{dto.status}
,isHigh=#{dto.isHigh}
, source = #{dto.source}
WHERE id= #{dto.id}

mapper.select:
SELECT * FROM POST WHERE blogId = #{id}

	AND status = #{status}
 ORDER BY id
