mapper.insert:
INSERT INTO POST(
title
, blogId
, img
, url
, created
, subContent
)VALUES(
#{dto.title}
, #{dto.blogId}
, #{dto.img}
, #{dto.url}
, NOW()
, #{dto.subContent}
)

mapper.update:
UPDATE POST SET
title = #{dto.title}
,blogId = #{dto.blogId}
,img = #{dto.img}
,content = #{dto.url}
,created = #{dto.created}
,subContent= #{dto.subContent}
WHERE id= #{dto.id}
