mapper.update:
UPDATE PRODUCT SET
code = #{dto.code}
, name = #{dto.name}
, tag = #{dto.tag}
, isNew = #{dto.isNew}
, isSale = #{dto.isSale}
, isEffete = #{dto.isEffete}

, img = #{dto.img}


, img1 = #{dto.img1}


, img2 = #{dto.img2}

, status = #{dto.status}
, categoryId = #{dto.categoryId}

, subCategoryId = #{dto.subCategoryId}

, price = (#{dto.price})

, salePrice = #{dto.salePrice}

, description = #{dto.description}

, count = #{dto.count}

WHERE id= #{dto.id}

mapper.select:
SELECT * FROM PRODUCT WHERE

	status IN(0,1)


	status = #{dto.status}


	AND categoryId = #{dto.categoryId}


	AND subCategoryId = #{dto.subCategoryId}


	AND created = #{dto.created}


	AND name = #{dto.name}


	AND code = #{dto.code}


	AND isNew = #{dto.sNew}


	AND isSale = #{dto.sSale}


	AND isEffete = #{dto.sEffete}

ORDER BY id
